    state ={
        title: "",
        comment: "",
    }

    handelChange =(e)=>{
        this.setState({
            [e.target.id]: e.target.value
        })
    }

    handleSubmit =(e) =>{
        e.preventDefault();
        console.log(this.state)
    }

    render() {
        return (
            <div className="container">
                <form onSubmit={this.handleSubmit} className="white">
                    <h5 className={"grey-text-darken-3"}>Write Post</h5>
                    <div className={input-field}>
                        <label htmlFor={title}>Title</label>
                        <input type="text" id="title" onChange={this.handelChange} />
                    </div>

                    <div className={"input-field"}>
                        <label htmlFor="content">Post Content</label>
                        <textarea id="content" className={"materialize-textarea"} onChange={this.handelChange}></textarea>
                    </div>

                    <div className={"input-field"}>
                        <button className={"btn blue lighter-1 z-depth-0"} >Post</button>
                    </div>
                </form>
            </div>
        )
    }
}

export default WritePost;
